import React, { Component } from "react";
import EmployeeDataService from "../services/employee.service";
import { Link } from "react-router-dom";

export default class EmployeeList extends Component {
    constructor(props) {
        super(props);
        this.onChangeSearchTitle = this.onChangeSearchTitle.bind(this);
        this.retrieveEmployees = this.retrieveEmployees.bind(this);
        this.refreshList = this.refreshList.bind(this);
        this.setActiveEmployee = this.setActiveEmployee.bind(this);
        this.removeAllEmployees = this.removeAllEmployees.bind(this);
        this.searchTitle = this.searchTitle.bind(this);

        this.state = {
            employees: [],
            currentEmployee: null,
            currentIndex: -1,
            searchTitle: ""
        };
    }

    componentDidMount() {
        this.retrieveEmployees();
    }

    onChangeSearchTitle(e) {
        const searchTitle = e.target.value;

        this.setState({
            searchTitle: searchTitle
        });
    }

    retrieveEmployees() {
        EmployeeDataService.getAll()
            .then(response => {
                this.setState({
                    employees: response.data
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    refreshList() {
        this.retrieveEmployees();
        this.setState({
            currentEmployee: null,
            currentIndex: -1
        });
    }

    setActiveEmployee(employee, index) {
        this.setState({
            currentEmployee: employee,
            currentIndex: index
        });
    }

    removeAllEmployees() {
        EmployeeDataService.deleteAll()
            .then(response => {
                console.log(response.data);
                this.refreshList();
            })
            .catch(e => {
                console.log(e);
            });
    }

    searchTitle() {
        EmployeeDataService.findByTitle(this.state.searchTitle)
            .then(response => {
                this.setState({
                    employees: response.data
                });
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    render() {
        const { searchTitle, employees, currentEmployee, currentIndex } = this.state;

        return (
            <div>
                <div className="list row">
                    <div className="col-md-6 col-sm-12 searchbox">
                        <b className="heading">AFFINITY ID</b>
                        <div className="input-group mb-3">
                            <input type="text" className="form-control"
                                placeholder="Search" value={searchTitle}
                                onChange={this.onChangeSearchTitle} />
                            <div className="input-group-append">
                                <button className="btn btn-outline-secondary"
                                    type="button" onClick={this.searchTitle}> Search </button>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-6 col-sm-12">
                        <div className="addbutton">
                            <Link to={"/add"} className="nav-link"> CREATE A NEW EMPLOYEE </Link>
                        </div>
                    </div>
                </div>

                <div className="list row">

                    <div className="col-md-6 col-sm-12">
                        <h4>Employee List</h4>

                        <ul className="list-group">
                            {employees &&
                                employees.map((employee, index) => (
                                    <li
                                        className={
                                            "list-group-item " +
                                            (index === currentIndex ? "active" : "")
                                        }
                                        onClick={() => this.setActiveEmployee(employee, index)}
                                        key={index}
                                    >
                                        {employee.title}
                                    </li>
                                ))}
                        </ul>
                    </div>

                    <div className="col-md-6 col-sm-12">
                        {currentEmployee ? (
                            <div>
                                <h4>Employee</h4>
                                <div>
                                    <label>
                                        <strong>Title:</strong>
                                    </label>{" "}
                                    {currentEmployee.title}
                                </div>
                                <div>
                                    <label>
                                        <strong>Description:</strong>
                                    </label>{" "}
                                    {currentEmployee.description}
                                </div>
                                <div>
                                    <label>
                                        <strong>Status:</strong>
                                    </label>{" "}
                                    {currentEmployee.published ? "Published" : "Pending"}
                                </div>

                                <Link                                    to={"/employees/" + currentEmployee.id}
                                    className="badge badge-warning"> Edit </Link>
                            </div>
                        ) : (
                                <div>
                                    <br />
                                    <p>Please click on a Employee...</p>
                                </div>
                            )}
                    </div>
                </div>
            </div>
        );
    }
}
